package com.cbfacademy.apiassessment.model;

import java.time.LocalDateTime;

import org.springframework.format.annotation.DateTimeFormat;

import jakarta.validation.constraints.Email;
import jakarta.validation.constraints.NotEmpty;
import jakarta.validation.constraints.PastOrPresent;

public record CustomerInfo(
    //TODO: CHANGE ID FROM LONG TO AUTOGENERATED UUID
    
    Long id,
    @NotEmpty
    String firstName,
    @NotEmpty
    String lastName,
    @NotEmpty
    @Email
    //must return for more specific email validation using regular expressions
    String email,
    //@UniqueElements - to make sure all elemts in the investment preferences list is unique
   // InvestmentPreferences investmentPreferences,
    Goal goal,
    RiskLevel riskLevel,
    Duration duration,
    StockSymbol stockSymbol,
    @DateTimeFormat
    @PastOrPresent
    LocalDateTime createdDate,
    @DateTimeFormat
    LocalDateTime updatedDate
) {
    
}
